// 默认设置对象
const DEFAULT_SETTINGS = {
  theme: 'system', // 默认主题：'light'（浅色模式）、'dark'（深色模式）、'system'（系统主题）
  blurAmount: 25, // 默认背景模糊度（百分比）
  searchBoxWidth: 600, // 默认搜索框宽度（像素）
  searchBoxHeight: 60, // 默认搜索框高度（像素）
  defaultEngine: 'default', // 默认搜索引擎
};

/**
 * 存储管理类，用于操作存储（如 Chrome 存储 API）中的数据
 */
class StorageManager {
  /**
   * 初始化存储
   * 该方法检查存储中是否有 "settings" 和 "searchEngines" 的数据，
   * 如果没有数据，则会初始化为默认值。
   * @returns {Promise<void>}
   */
  static async init() {
    try {
      // 获取当前存储中的设置、搜索引擎和分类数据
      const data = await chrome.storage.local.get(['settings', 'searchEngines', 'categories']);
      
      // 如果没有设置数据，使用默认设置初始化
      if (!data.settings) {
        await chrome.storage.local.set({
          settings: DEFAULT_SETTINGS
        });
      }

      // 如果没有搜索引擎数据，使用默认的搜索引擎列表初始化
      if (!data.searchEngines) {
        await chrome.storage.local.set({
          searchEngines: DEFAULT_SEARCH_ENGINES
        });
      }

      // 如果没有分类数据，使用默认的分类列表初始化
      if (!data.categories) {
        await chrome.storage.local.set({
          categories: DEFAULT_CATEGORIES
        });
      }
    } catch (error) {
      throw error;
    }
  }

  /**
   * 获取所有的设置
   * 该方法从存储中获取设置对象，若没有找到，则返回默认设置。
   * @returns {Promise<Object>  }
   */
  static async getSettings() {
    try {
      const data = await chrome.storage.local.get('settings');
      return data.settings || DEFAULT_SETTINGS; // 如果没有找到设置数据，返回默认值
    } catch (error) {
      return DEFAULT_SETTINGS; // 出错时返回默认设置
    }
  }

  /**
   * 更新设置
   * 该方法将新的设置与当前的设置合并，并更新存储中的数据。
   * @param {Object} newSettings - 包含新的设置的对象
   * @returns {Promise<Object>}
   */
  static async updateSettings(newSettings) {
    try {
      const currentSettings = await this.getSettings();
      
      // 将新的设置与当前设置合并
      const updatedSettings = { ...currentSettings, ...newSettings };
      
      await chrome.storage.local.set({
        settings: updatedSettings
      });
      
      return updatedSettings; 
    } catch (error) {
      throw error; 
    }
  }

  /**
   * 获取所有的搜索引擎
   * 该方法从存储中获取搜索引擎数据，若没有找到，则返回默认的搜索引擎列表。
   * @returns {Promise<Object>}
   */
  static async getSearchEngines() {
    try {
      const data = await chrome.storage.local.get('searchEngines');
      return data.searchEngines || DEFAULT_SEARCH_ENGINES; // 如果没有找到搜索引擎数据，返回默认值
    } catch (error) {
      return DEFAULT_SEARCH_ENGINES; // 出错时返回默认搜索引擎
    }
  }

  /**
   * 更新搜索引擎
   * 该方法更新存储中的搜索引擎数据。
   * @param {Object} newSearchEngines - 包含新搜索引擎数据的对象
   * @returns {Promise<void>}
   */
  static async updateSearchEngines(newSearchEngines) {
    try {
      // 如果传入的是数组，直接使用它
      if (Array.isArray(newSearchEngines)) {
        await chrome.storage.local.set({
          searchEngines: newSearchEngines
        });
      } else {
        // 如果传入的是对象，使用旧逻辑进行合并
        const currentEngines = await this.getSearchEngines();
        const updatedEngines = { ...currentEngines, ...newSearchEngines };
        await chrome.storage.local.set({
          searchEngines: updatedEngines
        });
      }
    } catch (error) {
      console.error('更新搜索引擎失败:', error);
      throw error;
    }
  }

  /**
   * 获取所有分类
   * 该方法从存储中获取分类数据，若没有找到，则返回默认的分类列表。
   * @returns {Promise<Object>}
   */
  static async getCategories() {
    const data = await chrome.storage.local.get('categories');
    return data.categories || DEFAULT_CATEGORIES; // 如果没有找到分类数据，返回默认值
  }

  /**
   * 更新分类
   * 该方法更新存储中的分类数据。
   * @param {Object} newCategories - 包含新分类数据的对象
   * @returns {Promise<void>}
   */
  static async updateCategories(newCategories) {
    const currentCategories = await this.getCategories();
    const updatedCategories = { ...currentCategories, ...newCategories };
    await chrome.storage.local.set({ categories: updatedCategories });
  }

  /**
   * 重置所有设置为默认值
   * 该方法将所有设置和搜索引擎数据重置为默认值。
   * @returns {Promise<void>}
   */
  static async resetToDefault() {
    try {
      await chrome.storage.local.set({
        settings: DEFAULT_SETTINGS,
        searchEngines: DEFAULT_SEARCH_ENGINES, // 同时重置搜索引擎到默认值
      });
    } catch (error) {
      throw error;
    }
  }

  /**
   * 重置搜索引擎为默认值
   * 该方法仅将搜索引擎数据重置为默认值。
   * @returns {Promise<void>}
   */
  static async resetSearchEngines() {
    try {
      await chrome.storage.local.set({
        searchEngines: DEFAULT_SEARCH_ENGINES
      });
      return DEFAULT_SEARCH_ENGINES;
    } catch (error) {
      console.error('重置搜索引擎失败:', error);
      throw error;
    }
  }

  /**
   * 生成唯一的搜索引擎ID
   * @returns {string} 生成的唯一ID
   */
  static generateEngineId() {
    return 'engine_' + Date.now() + '_' + Math.random().toString(15).substring(2, 8);
  }
}

export default StorageManager; // 导出 StorageManager 类供其他模块使用